import React from 'react';

export const CTX = React.createContext();


function reducer(state, action){
    const { name, weather, main} = action.payload;
    switch (action.type) {
        case "RECEIVE_LOCATION":
            console.log(name,weather,main);
            return ({
                name,
                weather,
                main,
            });

        default:
            return state;
    }
}

const initialState = fetch(`https://api.openweathermap.org/data/2.5/weather?zip=10022,us&units=imperial&appid=a673f727f21560d303446f34d91487b1`)
                    .then(res => {
                        return res;
                    }).then(res => {
                        const [defaultWeatherInfo, dispatch] = React.useReducer(reducer, initialState);
                        dispatch({ type: "RECEIVE_LOCATION", payload: res });
                    })




export default function Store(props){

    const [defaultWeatherInfo, dispatch] = React.useReducer(reducer, initialState);
    const fetchData = function (value) {
        fetch(`https://api.openweathermap.org/data/2.5/weather?q=${value}&units=imperial&appid=a673f727f21560d303446f34d91487b1`)
            .then(res => {
                return res.json();
            }).then(res => {
                dispatch({ type: "RECEIVE_LOCATION", payload: res });
            }
            );
    };
    return(
        <CTX.Provider value={{fetchData, defaultWeatherInfo}}>
            {props.children}
        </CTX.Provider>
    )
}
