import React from 'react';
import Typography from '@material-ui/core/Typography';

class WeatherInfoComponent extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            // name: '',
            // weather: '',
            // temp: '',
            // temp_max: '',
            // temp_min: '',
            // humidity: '',
            icon: `http://openweathermap.org/img/wn/${this.props.weather[0].icon}@2x.png`,
        };
    }

    // componentDidMount() {
