    render() {
        const {main, weather, name} = this.props.data;
        return (
            <div>
                <Typography variant="h4" component="h4">
                    {name}
                </Typography>

                    <img src={this.state.icon}/> 
                <Typography variant="h5" component="h5">
                    {weather[0].main}
                </Typography>

                <Typography variant="h5" component="h5" style={{ padding: '20px'}}>
                    {Math.ceil(main.temp) + '째'}
                </Typography>

                <Typography variant="subtitle1" component="h2">
                    Today's High: {main.temp_max}째 &nbsp; Today's Low: {main.temp_min}째
                </Typography>

                <Typography variant="subtitle2" component="h2" style={{padding: '10px'}}>
                    Humidity: {main.humidity}%
                </Typography>
            </div>
        )
    }
}

export default WeatherInfoComponent;
