import React from 'react';
import Typography from '@material-ui/core/Typography';

class WeatherInfo extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            weather: '',
            temp: '',
            temp_max: '',
            temp_min: '',
            humidity: '',
            icon: '',
        };

    }

    // componentDidMount() {
