    //                 }
    //             );
    //         });
    // }


    render() {
        console.log(this.props);
        const{main, weather, name} = this.props
        return (
            <div>
                <Typography variant="h4" component="h4">
                    {name}
                </Typography>

                    <img src={this.state.icon}/>
                <Typography variant="h5" component="h5">
                    {this.props.weather}
                </Typography>

                <Typography variant="h5" component="h5" style={{ padding: '20px'}}>
                    {Math.ceil(this.state.temp) + '째'}
                </Typography>

                <Typography variant="subtitle1" component="h2">
                    Today's High: {this.state.temp_max}째 &nbsp; Today's Low: {this.state.temp_min}째
                </Typography>

                <Typography variant="subtitle2" component="h2" style={{padding: '10px'}}>
                    Humidity: {this.state.humidity}%
                </Typography>
            </div>
        )
    }
}

export default WeatherInfo;
