import React from 'react';
import Typography from '@material-ui/core/Typography';

class WeatherInfo extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            weather: '',
            temp: '',
            temp_max: '',
            temp_min: '',
            humidity: '',
            icon: '',
        };
    }

    componentDidMount() {
        fetch(`https://api.openweathermap.org/data/2.5/weather?zip=10022,us&units=imperial&appid=a673f727f21560d303446f34d91487b1`)
            .then(res => {
                return res.json();
            }).then(res => {
                window.defaultLocation = res;
                this.setState(
                    {
                        name: res.name,
                        temp: res.main.temp,
                        temp_min: res.main.temp_min,
                        temp_max: res.main.temp_max,
                        humidity: res.main.humidity,
                        weather: res.weather[0].main,
                        icon: `http://openweathermap.org/img/wn/${res.weather[0].icon}@2x.png`
                    }
                );
            });
    }

    render() {
        return (
            <div>
                <Typography variant="h4" component="h4">
                    {this.state.name}
                </Typography>
                    <img src={this.state.icon}/>
                <Typography variant="h5" component="h5">
                    {Math.ceil(this.state.temp) + '째'}
                </Typography>
                <Typography variant="h6" component="h7">
                    Today's High: {this.state.temp_max}째 &nbsp; Today's Low: {this.state.temp_min}째
                </Typography>
            </div>
        )
    }
}

export default WeatherInfo;
