import React from 'react';

export const CTX = React.createContext();

function reducer(state, action){
    const { name, weather, main} = action.payload;
    switch (action.type) {
        case "RECEIVE_LOCATION":
            return ({
                name,
                weather,
                main,
            });

        default:
            return state;
    }
}

const initialState = '';



export default function Store(props){



    return(
        <CTX.Provider>
            {props.children}
        </CTX.Provider>
    )
}
