import React from 'react';

export const CTX = React.createContext();

function reducer(state, action){
    const { name, weather, main} = action.payload;
    switch (action.type) {
        case "RECEIVE_LOCATION":
            return ({
                name,
                weather,
                main,
            });

        default:
            return state;
    }
}

const initialState = '';

function fetchWeatherData(value) {
    fetch(`https://api.openweathermap.org/data/2.5/weather?q=${value},us&units=imperial&appid=a673f727f21560d303446f34d91487b1`)
    .then(res => {
        return res;
    }).then( res => {
        const [weatherInfo, dispatch] = React.useReducer(reducer, initialState);

        dispatch({type: "RECEIVE_LOCATION", payload: res})
    }
    );
}


export default function Store(props){

    const [weatherInfo, dispatch] = React.useReducer(reducer, initialState);

    return(
        <CTX.Provider value={{weatherInfo, fetchWeatherData}}>
            {props.children}
        </CTX.Provider>
    )
}
