import React from 'react';

import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';

import SimpleMap from './simple_map';

import Button from '@material-ui/core/Button';

import TextField from '@material-ui/core/TextField';
import WeatherInfo from './weather_info';


fetch(`https://api.openweathermap.org/data/2.5/weather?zip=10022,us&appid=a673f727f21560d303446f34d91487b1`)
    .then(res => {
        return res.json();
    }).then(res => {
        return res;
    });


const useStyles = makeStyles(theme => ({
    root: {
        padding: theme.spacing(3, 2),
        margin: '50px'
    },
    flex: {
        display: 'flex',
        alignItems: 'center',
        padding: '20px',
    },

    endTitle: {
        borderBottom: '1px solid grey'
    },

    weatherWindow: {
        width: '40%',
        height: '400px',

    },
    mapWindow: {
        width: '60%',
        height: '400px',
    },
    textField: {
        width: '100%',
        marginLeft: '40%'
    }
}));

export default function Dashboard() {
    const classes = useStyles();

    const [textValue, changeTextValue] = React.useState('');

    return (
        <div>
            <Paper className={classes.root}>
                <Typography variant="h4" component="h4">
                    Yarden's Weather App
                </Typography>

                <Typography variant="h5" component="h5" className={classes.endTitle}>
                    Find out what's the weather like around the world!
                </Typography>

                <div className={classes.flex}>
                    <div className={classes.weatherWindow}>
                        <WeatherInfo location={locationValue} />
                    </div>

                    <div id='map' className={classes.mapWindow}>
                        <SimpleMap />
                    </div>
                </div>

                <div className={classes.flex}>
                    <TextField
                        className={classes.textField}
                        id="standard-dense"
                        label="Location"
                        margin="dense"
                    />
                    <Button variant="contained" color="primary" className={classes.button}>
                        Search
                        </Button>
                </div>
            </Paper>
        </div>
    )
}
